import React from 'react';
import { SettingsProps, WallpaperSize, TimeFormat } from '../types';
import { WALLPAPER_CATEGORIES, ROTATION_INTERVALS, CSS_CLASSES } from '../utils/constants';
import { getCategoryDisplayName } from '../utils/imageLoader';
import '../styles/Settings.css';

/**
 * Settings component that provides user interface for configuration options
 * Appears on mouse movement and auto-hides after inactivity
 */
const Settings: React.FC<SettingsProps> = ({
  isVisible,
  onSettingsChange,
  currentSettings
}) => {
  // Handle clock enable/disable toggle
  const handleClockToggle = (enabled: boolean) => {
    onSettingsChange({ clockEnabled: enabled });
  };

  // Handle wallpaper enable/disable toggle
  const handleWallpaperToggle = (enabled: boolean) => {
    onSettingsChange({ wallpaperEnabled: enabled });
  };

  // Handle category selection
  const handleCategoryToggle = (category: string, selected: boolean) => {
    const updatedCategories = selected
      ? [...currentSettings.selectedCategories, category]
      : currentSettings.selectedCategories.filter(cat => cat !== category);

    onSettingsChange({ selectedCategories: updatedCategories });
  };

  // Handle wallpaper size change
  const handleWallpaperSizeChange = (size: WallpaperSize) => {
    onSettingsChange({ wallpaperSize: size });
  };

  // Handle rotation interval change
  const handleRotationIntervalChange = (interval: number) => {
    onSettingsChange({ rotationInterval: interval });
  };

  // Handle time format change
  const handleTimeFormatChange = (format: TimeFormat) => {
    onSettingsChange({ timeFormat: format });
  };

  // Get rotation interval label
  const getIntervalLabel = (value: number): string => {
    const interval = ROTATION_INTERVALS.find(int => int.value === value);
    return interval?.label || `${value / 1000}s`;
  };

  if (!isVisible) {
    return null;
  }

  return (
    <div className={`${CSS_CLASSES.SETTINGS_PANEL} ${CSS_CLASSES.SETTINGS_VISIBLE}`}>
      <div className="settings-content">
        <h2 className="settings-title">Settings</h2>

        {/* Display Mode Controls */}
        <div className="settings-section">
          <h3 className="settings-section-title">Display Mode</h3>

          <div className="settings-toggle-group">
            <label className="settings-toggle">
              <input
                type="checkbox"
                checked={currentSettings.clockEnabled}
                onChange={(e) => handleClockToggle(e.target.checked)}
              />
              <span className="settings-toggle-label">Show Clock</span>
            </label>

            <label className="settings-toggle">
              <input
                type="checkbox"
                checked={currentSettings.wallpaperEnabled}
                onChange={(e) => handleWallpaperToggle(e.target.checked)}
              />
              <span className="settings-toggle-label">Show Wallpapers</span>
            </label>
          </div>
        </div>

        {/* Time Format */}
        <div className="settings-section">
          <h3 className="settings-section-title">Time Format</h3>

          <div className="settings-radio-group">
            <label className="settings-radio">
              <input
                type="radio"
                name="timeFormat"
                value="12h"
                checked={currentSettings.timeFormat === '12h'}
                onChange={() => handleTimeFormatChange('12h')}
              />
              <span className="settings-radio-label">12 Hour</span>
            </label>

            <label className="settings-radio">
              <input
                type="radio"
                name="timeFormat"
                value="24h"
                checked={currentSettings.timeFormat === '24h'}
                onChange={() => handleTimeFormatChange('24h')}
              />
              <span className="settings-radio-label">24 Hour</span>
            </label>
          </div>
        </div>

        {/* Wallpaper Settings */}
        {currentSettings.wallpaperEnabled && (
          <>
            {/* Wallpaper Size */}
            <div className="settings-section">
              <h3 className="settings-section-title">Wallpaper Size</h3>

              <div className="settings-radio-group">
                {(['small', 'medium', 'large'] as WallpaperSize[]).map(size => (
                  <label key={size} className="settings-radio">
                    <input
                      type="radio"
                      name="wallpaperSize"
                      value={size}
                      checked={currentSettings.wallpaperSize === size}
                      onChange={() => handleWallpaperSizeChange(size)}
                    />
                    <span className="settings-radio-label">
                      {size.charAt(0).toUpperCase() + size.slice(1)}
                    </span>
                  </label>
                ))}
              </div>
            </div>

            {/* Rotation Interval */}
            <div className="settings-section">
              <h3 className="settings-section-title">Rotation Speed</h3>

              <select
                className="settings-select"
                value={currentSettings.rotationInterval}
                onChange={(e) => handleRotationIntervalChange(Number(e.target.value))}
              >
                {ROTATION_INTERVALS.map(interval => (
                  <option key={interval.value} value={interval.value}>
                    {interval.label}
                  </option>
                ))}
              </select>
            </div>

            {/* Category Selection */}
            <div className="settings-section">
              <h3 className="settings-section-title">Wallpaper Categories</h3>

              <div className="settings-category-grid">
                {WALLPAPER_CATEGORIES.map(category => (
                  <label key={category.name} className="settings-category">
                    <input
                      type="checkbox"
                      checked={currentSettings.selectedCategories.includes(category.name)}
                      onChange={(e) => handleCategoryToggle(category.name, e.target.checked)}
                    />
                    <span className="settings-category-label">
                      {getCategoryDisplayName(category.name)}
                    </span>
                  </label>
                ))}
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Settings;
